INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'lv_LV', '{WEB_TITLE} - Kādam ir jautājums par jūsu sludinājumu', '<p>Sveicināti, {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) atstāja jums ziņojumu par jūsu sludinājumu <a href="{ ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>Ar cieņu,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'lv_LV', 'Lūdzu, apstipriniet savu {WEB_TITLE} kontu', '<p>Sveicināti, {USER_NAME}!</p><p>Lūdzu, apstipriniet savu reģistrāciju, noklikšķinot uz šīs saites: {VALIDATION_LINK}</p><p>Paldies!< /p><p>Ar cieņu</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'lv_LV', '{WEB_TITLE} — reģistrācija ir veiksmīga!', '<p>Sveicināti, {USER_NAME}!</p><p>Jūs esat veiksmīgi reģistrējies vietnei {WEB_LINK}.</p><p>Paldies!</p> <p>Ar cieņu,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'lv_LV', 'Apskatiet, ko es atklāju vietnē {WEB_TITLE}', '<p>Sveicināti, {FRIEND_NAME}!</p><p>Jūsu draugs {USER_NAME} vēlas koplietot šo sludinājumu ar jums <a href="{ITEM_URL}">{ ITEM_TITLE}</a>.</p><p>Ziņojums:</p><p>{COMMENT}</p><p>Ar cieņu</p><p>{WEB_TITLE}</p> );
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'lv_LV', '{WEB_TITLE} — Jauni sludinājumi pēdējās stundas laikā', '<p>Sveicināti, {USER_NAME}!</p><p>Jauni sludinājumi ir publicēti pēdējā stundā. Apskatiet tos:</p><p>{ADS}</p><p>--------------</p><p>Lai anulētu šo paziņojumu abonementu, noklikšķiniet uz saites: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'lv_LV', '{WEB_TITLE} — Jauni sludinājumi šodien', '<p>Sveicināti, {USER_NAME}!</p><p>Jauni sludinājumi ir publicēti pēdējā dienā. Apskatiet tos:</p><p>{ADS}</p><p>--------------</p><p>Lai anulētu šo paziņojumu abonementu, noklikšķiniet uz saites: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'lv_LV', '{WEB_TITLE} - Jauni sludinājumi pagājušajā nedēļā', '<p>Sveicināti, {USER_NAME}!</p><p>Pagājušajā nedēļā ir publicēti jauni sludinājumi. Apskatiet tos:</p><p>{ADS}</p><p>-------------</p><p>Lai anulētu šo paziņojumu abonementu, noklikšķiniet uz saites: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'lv_LV', '{WEB_TITLE} - Jauni sludinājumi', '<p>Sveicināti, {USER_NAME}!</p><p>Jauns sludinājums ir publicēts, pārbaudiet to!</p><p>{ADS}</p><p>-- -----------</p><p>Lai anulētu šo paziņojumu abonementu, noklikšķiniet uz saites: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'lv_LV', '{WEB_TITLE} — Jauns komentārs', <p>Kāds komentēja sludinājumu <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Komentētājs: {COMMENT_AUTHOR}<br / >Komentētāja e-pasts: {COMMENT_EMAIL}<br />Nosaukums: {COMMENT_TITLE}<br />Komentārs: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'lv_LV', '{WEB_TITLE} — Sludinājuma {ITEM_TITLE} rediģēšanas opcijas', <p>Sveicināti, {USER_NAME}!</p><p>Jūs neesat reģistrējies vietnē {WEB_LINK}, taču jūs joprojām varat rediģēt vai dzēst sludinājumu <a href="{ITEM_URL}">{ITEM_TITLE}</a> uz īsu laiku.</p><p>Jūs varat rediģēt savu sludinājumu, izmantojot šo saiti: {EDIT_LINK}</p><p >Jūs varat izdzēst savu sludinājumu, izmantojot šo saiti: {DELETE_LINK}</p><p>Ja reģistrējaties kā lietotājs, jums būs pilna piekļuve rediģēšanas opcijām.</p><p>Ar cieņu</p> <p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'lv_LV', '{WEB_TITLE} — Apstipriniet savu sludinājumu', '<p>Sveicināti, {USER_NAME}!</p><p>Jūs saņēmāt šo e-pasta ziņojumu, jo sludinājums ir publicēts vietnē {WEB_LINK}. Lūdzu, apstipriniet šo sludinājumu, noklikšķinot uz šīs saites: {VALIDATION_LINK}. Ja jūs nepublicējāt šo sludinājumu, lūdzu, ignorējiet šo e-pasta ziņojumu.</p><p>Informācija par sludinājumu:</p><p>Kontaktpersonas vārds: {USER_NAME}<br />Kontaktpersonas e-pasts: {USER_EMAIL} </p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Ar cieņu</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'lv_LV', '{WEB_TITLE} — Jauns sludinājums ir publicēts', <p>Cien., {WEB_TITLE} Administrator!</p><p>Jūs saņēmāt šo e-pasta ziņojumu, jo sludinājums ir publicēts vietnē {WEB_LINK}.</p ><p>Informācija par sludinājumu:</p><p>Kontaktpersonas vārds: {USER_NAME}<br />Kontaktpersonas e-pasts: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: { ITEM_URL}</p><p>Varat rediģēt šo sludinājumu, noklikšķinot uz šīs saites: {EDIT_LINK}</p><p>Ar cieņu</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'lv_LV', '{WEB_TITLE} — Atkopiet savu paroli', '<p>Sveicināti, {USER_NAME}!</p><p>Mēs nosūtījām jums šo e-pasta ziņojumu, jo pieprasījāt paroles atgādinājumu. Lai to atgūtu, izmantojiet šo saiti: {PASSWORD_LINK}</p><p>Saite tiks deaktivizēta 24 stundu laikā.</p><p>Ja nepieprasījāt paroles atgādinājumu, lūdzu, ignorējiet šo ziņojumu. Šis pieprasījums tika nosūtīts no IP {IP_ADDRESS} {DATE_TIME}</p><p>Ar cieņu</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'lv_LV', '{WEB_TITLE} — Jūs pieprasījāt e-pasta izmaiņas', '<p>Sveicināti, {USER_NAME}</p><p>Jūs saņēmāt šo e-pasta ziņojumu, jo pieprasījāt e-pasta izmaiņas. Lūdzu, apstipriniet šo jauno e-pasta adresi, noklikšķinot uz šīs validācijas saites: {VALIDATION_LINK}</p><p>Ar cieņu</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'lv_LV', '{WEB_TITLE} — Lūdzu, apstipriniet savu paziņojumu', "<p>Sveicināti, {USER_NAME}!</p><p>Lūdzu, apstipriniet savu paziņojuma reģistrāciju, noklikšķinot uz šīs saites: {VALIDATION_LINK}</p><p>Paldies jums!</p><p>Ar cieņu,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'lv_LV', '{WEB_TITLE} — Jūsu komentārs ir apstiprināts', '<p>Sveicināti, {COMMENT_AUTHOR}!</p><p>Jūsu komentārs par <a href="{ITEM_URL}">{ITEM_TITLE}</a> ir apstiprināts.</p><p>Ar cieņu</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'lv_LV', '{WEB_TITLE} — Apstipriniet savu sludinājumu', '<p>Sveicināti, {USER_NAME}!</p><p>Jūs saņēmāt šo e-pasta ziņojumu, jo esat publicējis sludinājumu vietnē {WEB_LINK}. Lūdzu, apstipriniet šo sludinājumu, noklikšķinot uz šīs saites: {VALIDATION_LINK}. Ja jūs nepublicējāt šo sludinājumu, lūdzu, ignorējiet šo e-pastu.</p><p>Informācija par sludinājumu:</p><p>Kontaktpersonas vārds: {USER_NAME}<br />Kontaktpersonas e-pasts: { USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Pat ja neesat reģistrēts vietnē {WEB_LINK}, jūs joprojām varat rediģēt vai dzēst jūsu sludinājumu:</p><p>Jūs varat rediģēt savu sludinājumu, izmantojot šo saiti: {EDIT_LINK}</p><p>Jūs varat izdzēst savu sludinājumu, izmantojot šo saiti: {DELETE_LINK}</p><p> Ar cieņu,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'lv_LV', '{WEB_TITLE} — Ir reģistrējies jauns lietotājs', '<p>Cien., {WEB_TITLE} Administrator!</p><p>Jūs saņēmāt šo e-pasta ziņojumu, jo vietnē {WEB_LINK} ir izveidots jauns lietotājs.</p> p><p>Lietotāja informācija:</p><p>Vārds: {USER_NAME}<br />E-pasts: {USER_EMAIL}</p><p>Ar cieņu</p><p>{WEB_LINK} </p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'lv_LV', '{WEB_TITLE} — Kādam ir jums jautājums', '<p>Sveicināti, {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) atstāja jums ziņojumu:</p> <p>{COMMENT}</p><p>Ar cieņu,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'lv_LV', '{WEB_TITLE} — Kāds ir komentējis jūsu sludinājumu', '<p>Sludinājummam ir jauns komentārs: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p>< p>Autors: {COMMENT_AUTHOR}<br />Autora e-pasta adrese: {COMMENT_EMAIL}<br />Nosaukums: {COMMENT_TITLE}<br />Komentārs: {COMMENT_TEXT}</p><p>{WEB_LINK}</ p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'lv_LV', '{WEB_TITLE} — Veiksmīgi izveidots administratora konts!', '<p>Sveicināti, {ADMIN_NAME}!</p><p>Vietnes {WEB_LINK} administrators ir izveidojis jums kontu.</p><ul><li >Lietotājvārds: {USERNAME}</li><li>Parole: {PASSWORD}</li></ul><p>Administratora panelim varat piekļūt šeit {WEB_ADMIN_LINK}.</p><p>Paldies! </p><p>Ar cieņu,</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'lv_LV', "{WEB_TITLE} — Jūsu reklāmas publicēšanas termiņš drīz beigsies', "<p>Sveicināti, {USER_NAME}!</p><p>Jūsu sludinājums <a href="{ITEM_URL}">{ITEM_TITLE}</a> publicēšanas termiņš beigsies {WEB_LINK}.');
